mapper.select:
SELECT a.id, a.phone_number, a.report_or_allot, a.report_or_allot_date, a.user_identify, c.actual_name, a.if_performance_pool, b.version_no, b.start_date, b.end_date FROM cl_formal_user AS a JOIN cl_formal_user_version AS b ON a.id = b.user_id JOIN kam_advisor AS c ON b.advisor_id = c.id LEFT OUTER JOIN (SELECT user_id,max(version_no) AS max_version_no FROM cl_formal_user_version GROUP BY user_id) AS d ON a.id = d.user_id LEFT OUTER JOIN (SELECT id,user_id,advisor_id FROM cl_formal_user_version WHERE
(start_date<=#{dateStart} AND coalesce(end_date,date(now()))>=#{dateEnd}) OR (start_date<=#{dateStart} AND coalesce(end_date,date(now()))<=#{dateEnd} AND #{dateStart}<=coalesce(end_date,date(now()))) OR (start_date>=#{dateStart} AND coalesce(end_date,date(now()))<=#{dateEnd}) OR (start_date>=#{dateStart} AND coalesce(end_date,date(now()))>=#{dateEnd} AND #{dateEnd}>=start_date)
) AS e ON b.id = e.id WHERE a.if_delete = 0 AND e.id IS NOT NULL
AND a.phone_number = #{search}
AND d.user_id IS NOT NULL
ORDER BY a.phone_number, b.start_date DESC
